mapper.select:
SELECT seek.id, sequence, seek_type, miss_name, gender, birthdate, miss_date, miss_detail_place, feature, seek_status, plot, seek_subtype, contact_name, relationship, contact_tel, contact_wechat, contact_QQ, contact_address, extra_tel, other_information, title, pubdate, GROUP_CONCAT(seek_img.url SEPARATOR ',') AS seekimgs, bp.province_name AS birth_province_name, bc.city_name AS birth_city_name, boy.county_name AS birth_county_name, mp.province_name AS miss_province_name, mc.city_name AS miss_city_name, my.county_name AS miss_county_name FROM seek LEFT JOIN seek_img ON seek.id=seek_img.seek_id LEFT JOIN province bp ON seek.birth_province_id=bp.id LEFT JOIN city bc ON seek.birth_city_id=bc.id LEFT JOIN county boy ON seek.birth_county_id=boy.id LEFT JOIN province mp ON seek.miss_province_id=mp.id LEFT JOIN city mc ON seek.miss_city_id=mc.id LEFT JOIN county my ON seek.miss_county_id=my.id WHERE 1=1
AND miss_name =#{seek.missName} <<<<<<< HEAD AND miss_name like %${seek.missName}% ======= >>>>>>> 04b535099ae8f956d170c1de801c454b9ef8af0a
AND gender =#{seek.gender}
AND seek_subtype =#{seek.seekSubtype}
AND seek_type =#{seek.seekType}
AND birthdate =#{seek.birthdate}
AND miss_date =#{seek.missDate}
AND seek_status =#{seek.seekStatus}
AND miss_province_id =#{seek.address.missProvinceId}
AND miss_city_id =#{seek.address.missCityId}
AND miss_county_id =#{seek.address.missCountyId}
